CREATE TABLE if not exists EncephUser(id bigint(20) NOT NULL,UserName varchar(20) DEFAULT NULL,enabled boolean not null default true,Password varchar(50),FirstName varchar(20) DEFAULT NULL,LastName varchar(20) DEFAULT NULL,Age int DEFAULT 0,Gender ENUM('Male','Female','Transgender','Unknown'),AddressId varchar(10) DEFAULT 'Future Use',Religon varchar(20) DEFAULT NULL,PlaceOfBirth varchar(20) DEFAULT NULL,DateOdBirth Date ,TimeOfBirth TIME,PRIMARY KEY (id),CONSTRAINT UniqueUserName UNIQUE (UserName)) ENGINE=InnoDB DEFAULT CHARSET=latin1
CREATE TABLE if not exists EncephUserRoles(id bigint(20) NOT NULL,UserName varchar(20) NOT NULL,Role varchar(20) DEFAULT 'ROLE_USER',PRIMARY KEY (id),KEY EncephUserCons (UserName),CONSTRAINT EncephUserCons FOREIGN KEY (UserName) REFERENCES EncephUser (UserName)) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS QuestionUserIntraction(UserId bigint(20) NOT NULL,QuestionId bigint(20) NOT NULL,QuestionType varchar(20) DEFAULT NULL,LastAsked DATETIME default current_date(),TimesAsked int DEFAULT 1,TimesCorrect int DEFAULT 0,DifficultyCatagory ENUM('Hardest','Harder','Hard','Normal','Easy','Easier','Easiest'),PRIMARY KEY (UserId,QuestionId),KEY UserIdKey(UserId),CONSTRAINT UserIdKey FOREIGN KEY (UserId)REFERENCES EncephUser(id),KEY QuestionIdKey(QuestionId),CONSTRAINT QuestionIdKey FOREIGN KEY (QuestionId)REFERENCES Question (id)) ENGINE=InnoDB DEFAULT CHARSET=latin1